import pandas as pd
import numpy as np
import ta
from engine.strategy import BaseStrategy, Signal

class RSIMeanReversionStrategy(BaseStrategy):
    def on_bar(self, bar, history):
        # Need at least RSI period + 1 bars to calculate RSI
        if len(history) < 15:
            return None

        # Calculate RSI
        rsi = ta.momentum.RSIIndicator(history['close'], window=14).rsi()
        current_rsi = rsi.iloc[-1]
        prev_rsi = rsi.iloc[-2]
        current_price = bar['close']

        # Buy signal: RSI crosses above oversold threshold (30)
        if prev_rsi <= 30 and current_rsi > 30:
            stop_loss = current_price * (1 - 0.01)  # 1% stop loss
            return Signal(action='buy', size=1.0, stop_loss=stop_loss)

        # Sell signal: RSI crosses above overbought threshold (70)
        if prev_rsi <= 70 and current_rsi > 70:
            return Signal(action='close')

        return None
