import pandas as pd
import numpy as np
import ta
from engine.strategy import BaseStrategy, Signal

class RSIMeanReversionStrategy(BaseStrategy):
    def on_bar(self, bar, history):
        if len(history) < 15:
            return None

        rsi = ta.momentum.RSIIndicator(history['close'], window=14).rsi()
        current_rsi = rsi.iloc[-1]
        prev_rsi = rsi.iloc[-2]

        # Buy when RSI crosses above 30
        if prev_rsi <= 30 and current_rsi > 30:
            return Signal(action='buy', size=1.0)

        # Sell when RSI crosses above 70
        if prev_rsi <= 70 and current_rsi > 70:
            return Signal(action='close')

        return None
